css:
hr.po-line {
    display: block;
    height: 1px;
    background: transparent;
    width: 100%;
    border: none;
    border-top: solid 1px #aaa;
}


table.po-caption  tbody tr td:nth-child(2)::before {
            content: ": ";
    }

table.po-caption  tbody tr td:nth-child(4)::before {
            content: ": ";
    }    

table.po-info  tbody tr td:nth-child(2)::before {
            content: ": ";
    }

table.signer tbody td {
        width: 45px;
}


.judul {
 margin-bottom: 50px;
 text-align: center;
}


tr.no-space > td {
  border: none !important;
  padding: 2px !important;
}

tr.po-body > td {
 padding: 4px !important;
}

tr.content {
    height:60px;
}

